func TestSubmitRequest(t *testing.T) {
	s := SubmitRequest{
		TxBlob: "",
	}

	j := `{
	"tx_blob": ""
}`
	if err := test.SerializeAndDeserialize(t, s, j); err != nil {
		t.Error(err)
	}

}

